import React, { Component } from 'react';
import {ecommerceProducts,detailProduct} from './data';
const ProductContext= React.createContext();

class ProductProvider extends Component {
  state={
    products:[],
    detailProduct:detailProduct
  }
  ComponentDidMount(){
    this.setProducts();
  }
  setProducts = () => {
    let tempProducts=[];
    ecommerceProducts.forEach(item =>{
      const singleItem = {...item};
      tempProducts = [...tempProducts,singleItem];
    });
    this.setState(() =>{
      return { products :tempProducts};
    });
  }

  handleDetails = () => {
    console.log('Hello from details function ');
  }

  addToCart = () => {
    console.log('Hello from addToCart function ');

  }

  render(){
    return(
      <ProductContext.Provider value={{...this.state,handleDetails:this.handleDetails,addToCart:this.addToCart}}>
        {this.props.children}
      </ProductContext.Provider>
    );
  }
}

const ProductConsumer = ProductContext.Consumer;
export {ProductProvider , ProductConsumer};
