import React, { Component } from 'react';
import {ProductConsumer} from '../context';
import {Link} from 'react-router-dom';

export default class Details extends Component {
  render() {
    return(
      <ProductConsumer>
        {value => {
          const {id,company,img,info,price,title,inCart} =value.detailProduct;
          return(
            <div className="card mx-auto col-md-10 my-4" style={{width: "100%",paddingTop:"3.0rem"}}>
              <h5 className="card-title text-center"> <h1>{title}</h1> </h5>
              <hr/>
              <div className="card-body">
                <div className="row">
                <div className="col-md-6">
                  <img src={img} className="card-img-top img-responsive" alt={title}/>
                </div>
                <div className="col-md-6">
                  <p className="card-text">{info}</p>
                  <hr/>
                    <p className="card-text"> Brand : {company}</p>
                  <div className="clearfix">
                    <Link to="" className="btn btn-outline-primary float-left" onClick={() => console.log("You clicked na !")}>Price : ${price}</Link>
                    <button className="btn btn-success float-right"><i class="fas fa-money-bill-alt"></i> Buy </button>
                  </div>
                </div>
              </div>
              </div>
            </div>
          );
        }}
      </ProductConsumer>
    )
  }
}
