import React, { Component } from 'react';
import {Link} from 'react-router-dom';
import {ProductConsumer} from '../context';
import PropTypes from 'prop-types'

export default class Product extends Component {
  render() {
    const {id,title,img,price,inCart} = this.props.product;
    return(
      <div className="col-md-3">
        <ProductConsumer>
          {value =>(
            <div className="card mx-auto my-2" onClick={() => console.log("Added to cart !")} style={{width: "20rem",padding:"0.5rem"}}>
            <Link to="/details"><img src={img} className="card-img-top" alt={title}/></Link>
            <div className="card-body">
              <h5 className="card-title">{title}</h5>
              <p className="card-text">Price : ${price}</p>
              <hr/>
              <div className="clearfix">
                <Link to="/details" className="btn btn-outline-primary float-left" onClick={() => console.log("Added to cart !")}>Details</Link>
                <button className="btn btn-outline-info float-right" disabled={inCart ? true :false} >{inCart?'In ':<i className="fas fa-shopping-bag"></i>}  Cart</button>
              </div>
            </div>
          </div>
          )}
        </ProductConsumer>
    </div>
    )
  }
}

Product.propTypes ={
  product:PropTypes.shape({
    id:PropTypes.number,
    img:PropTypes.string,
    title:PropTypes.string,
    price:PropTypes.number,
    inCart:PropTypes.bool
  }).isRequired
}
