import React, { Component } from 'react';
import {Link} from 'react-router-dom';
import {ProductConsumer} from '../context';


export default class Modal extends Component {
  render() {
    return(
      <ProductConsumer>
      {(value) =>{
        const {modalOpen,closeModal} = value;
        const {img,title,price} = value.modalProduct;

        if(!modalOpen){
          return null;
        }
        else {
          return(
          <div className="card mx-auto h-50 " style={{width: "20rem",padding:"0.5rem",position:"fixed",top:"0",left:"0",right:"0",bottom:"0",margin:"250px 0 0 0"}}>
            <h5 className="card-title text-center alert alert-success" role="alert">Added to cart</h5>
          <img src={img} className="card-img-top" alt={title}/>
            <div className="card-body">
              <h5 className="card-title">{title}</h5>
              <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
            </div>
          </div>
        );
        }
      }}
      </ProductConsumer>
    )
  }
}
